import { AllServicesService } from './../../services/all-services.service';
import { Component } from '@angular/core';
import { ActivatedRoute, Params } from '@angular/router';

@Component({
  selector: 'app-movie-details',
  templateUrl: './movie-details.component.html',
  styleUrls: ['./movie-details.component.css']
})
export class MovieDetailsComponent {

  id: any

  selectedMovie:any

  constructor(private route: ActivatedRoute, private service: AllServicesService) { }

  ngOnInit() {
    this.route.params.subscribe(paramsId => {
      this.id = paramsId['id'];
      console.log(this.id);
    });

    this.getAllMovie()
  }



  getAllMovie() {
    this.service.getPopularMovie().subscribe(response=>{
      let data = JSON.parse(JSON.stringify(response)).details

      this.selectedMovie = data.filter((x:any)=>{
        return x.title == this.id
      })

      console.log(this.selectedMovie)
    })
  }
}
